using System.Collections.Generic;using Card;using UnityEngine;public class GameStorage : MonoBehaviour{    private readonly List<FounderCard> _founderCards = new();    private readonly List<CompanyCard> _companyCards = new();    private readonly List<ImpactPoint> _impactPoints = new();    public GameStorage()    {        InitializeFounderCards();        InitializeCompanyCards();        InitializeImpactPoints();    }    public IEnumerable<FounderCard> GetFounderCards()    {        return _founderCards;    }    public IEnumerable<CompanyCard> GetCompanyCards()    {        return _companyCards;    }    public IEnumerable<ImpactPoint> GetImpactPoints()    {        return _impactPoints;    }    private void InitializeFounderCards()    {        _founderCards.Add(new FounderCard("COLLEGE DROPOUT", SuperPowerSkill.Programming, 20000));        _founderCards.Add(new FounderCard("FORMER DESIGNER", SuperPowerSkill.Design, 20000));        _founderCards.Add(new FounderCard("FORMER ACCOUNTANT", SuperPowerSkill.Accounting, 20000));        _founderCards.Add(new FounderCard("FORMER LAWYER", SuperPowerSkill.Legal, 40000));        _founderCards.Add(new FounderCard("FORMER CONSULTANT", SuperPowerSkill.Marketing, 0));        _founderCards.Add(new FounderCard("FORMER PUBLIC RELATIONS", SuperPowerSkill.PublicRelations, 0));        _founderCards.Add(new FounderCard("FORMER SALES EXEC", SuperPowerSkill.Sales, 0));        _founderCards.Add(new FounderCard("SERIAL ENTREPRENEUR", SuperPowerSkill.YourChoice, 100000));        _founderCards.Add(new FounderCard("COLLEGE STUDENT", SuperPowerSkill.Design, 0));        _founderCards.Add(new FounderCard("PROGRAMMER", SuperPowerSkill.Programming, 40000));        _founderCards.Add(new FounderCard("FORMER ACTRESS", SuperPowerSkill.Marketing, 0));    }    private void InitializeCompanyCards()    {        _companyCards.Add(new CompanyCard("SOCIAL NETWORK", 40000));        _companyCards.Add(new CompanyCard("SUPER SECRET SCIENCE TECH", 60000));        _companyCards.Add(new CompanyCard("TRANSPORTATION", 60000));        _companyCards.Add(new CompanyCard("EDUCATION TECH", 40000));        _companyCards.Add(new CompanyCard("ENTERTAINMENT & GAMING", 60000));        _companyCards.Add(new CompanyCard("MEDIA & MARKETING", 40000));        _companyCards.Add(new CompanyCard("REAL ESTATE", 60000));        _companyCards.Add(new CompanyCard("RETAIL", 40000));        _companyCards.Add(new CompanyCard("BUSINESS-TO-CONSUMER (B2C)", 40000));        _companyCards.Add(new CompanyCard("BUSINESS-TO-BUSINESS (B2B)", 60000));    }    private void InitializeImpactPoints()    {        _impactPoints.Add(new ImpactPoint("Social", "Your company is growing and creating jobs", 10));        _impactPoints.Add(new ImpactPoint("Ethics and Diversity", "The workplace is diverse and inclusive", 10));        _impactPoints.Add(new ImpactPoint("Sustainability", "The office energy bills are through the roof", -10));        _impactPoints.Add(new ImpactPoint("Health & Wellness",            "Not enough sleep. Drinking 10 cups of coffee to stay awake", -10));    }}